import java.text.{SimpleDateFormat, ParseException}
import java.util.Date

import org.apache.spark.{SparkConf}
import org.apache.spark.streaming.kafka.{KafkaUtils}
import org.apache.spark.streaming.{StreamingContext, Seconds}

import play.api.libs.json._

val ssc = new StreamingContext(sc, Seconds(2))
val msgStream = KafkaUtils.createStream(ssc, "master.mesos:2181/dcos-service-kafka", "zeppelin-consumer-group", Map("tweets" -> 1)).map(_._2)

val tweetStream = msgStream.map(tweetString => {
        val tweet = Json.parse(tweetString)
        ((tweet \ "handle").as[String], (tweet \ "content").as[String], (tweet \ "created_at").as[String])
    })
tweetStream.print()

tweetStream.window(Seconds(60))
    .foreachRDD(rdd => rdd.toDF("handle", "content", "created_at").registerTempTable("tweets"))



ssc.start()
